// ==UserScript==
// @name         Feeling Lucky Linker
// @namespace    http://dragonshobbies.com/
// @version      0.3
// @description  Test loading random pages for casual spot-check purposes.
// @author       Nathaniel "Dragon"
// @match        dragonshobbies.com/*
// @match        *.dragonshobbies.com/*
// @match        http://*
// @match        https://*
// @grant        none
// ==/UserScript==

var timer = 60; // seconds between page loads

async function goToNextPage(page) {
    window.location.href=page;
}

async function runSpotCheck(timer) {
  var page = prepSpotCheck();
  createDisplayBox();
  addDisplay(page, timer);
  console.log('Just a moment...');
  while(timer > 4) {
      await sleep(1000); // 1 second
      // console.log(timer);
      timer = timer - 1;
      addDisplay(page, timer);
  }
  while(timer > 1) {
      await sleep(1000); // 1 second
      timer = timer - 1;
      addCountdownDisplay(timer);
  }
  console.log('Load next page...');
  goToNextPage(page);
}

function addCountdownDisplay(timer) {
    var displayBox = document.getElementById('luckyPunk');
    displayBox.innerHTML = '...' + timer + '...';
    displayBox.style = 'z-index: 5000; position: absolute; top: 0; left: 0; background-color: #FF9; color: #F00; text-align: center; width: 100%; font-size: 3em';
    // document.body.appendChild(displayBox);
    beep();
}

function addDisplay(page, timer) {
    var displayBox = document.getElementById('luckyPunk');
    displayBox.innerHTML = 'Are you feeling lucky, punk? Next up: <br>' + page + '<br>in ' + timer + ' seconds.';
    displayBox.style = 'z-index: 5000; position: absolute; top: 0; left: 0; background-color: #FF9; color: #000; text-align: center; width: 100%;';
    // document.body.appendChild(displayBox);
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

function createDisplayBox() {
    var displayBox = document.createElement('div');
    displayBox.id = 'luckyPunk';
    document.body.appendChild(displayBox);
}

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

function prepSpotCheck() {
    var arr = [], links = document.links, r = 0, page = '';
    for(var i=0; i<links.length; i++) {
        arr.push(links[i].href);
        // console.log(links[i].href);
    }
    console.log('There are ' + arr.length + ' links on this page.');
    if(arr.length > 0) {
        r = Math.floor(Math.random() * arr.length);
        page = arr[r];
        if(page.indexOf('#') > -1 || page.indexOf('.jpg') > -1 || page.indexOf(window.location.host) == -1) {
            page = arr[0];
            console.log('Try the first link.');
        }
        while(page.indexOf('#') > -1 || page.indexOf('.jpg') > -1 || page.indexOf(window.location.host) == -1) {
            r = r + 1;
            page = arr[r];
            console.log('Pick again.');
        }
        console.log('We will go to #' + r + ' of ' + arr.length + ': ' + page);
    } else {
        page = 'http://' + window.location.host;
    }
    return page;
}

runSpotCheck(timer);

